import React, { Component } from 'react';
import './App.css';
import CardCountry from './components/CardCountry';
import Header from './components/Header';
import Search from './components/Search';
import Country from "./CountryDb";

class App extends Component {
  constructor() {
    super()
    this.state = {
      paises: undefined,
      pais: '',
      local: '',
      meta: '',
      cards: [{ name: 'Brasil', flag: 'https://restcountries.eu/data/bra.svg' , local: 'um ai' , meta: '2020-05-02' }, { name: 'Afeganistao', flag: 'https://restcountries.eu/data/afg.svg' , local: 'Terrorista' , meta: '2025-09-15' }],
      edit: { isEdit: 0, indice: 0 }
    }
  }

  async componentDidMount() {
    const paises = await Country();
    this.setState({ paises: paises })
  }

  handleInput(e) {
    const value = e.target.value;
    const key = e.target.name;
    this.setState({ [key]: value })
  }

  handleAdd() {
    const { paises, pais, local, meta, cards } = this.state;
    const umPais = paises.filter(item => item.alpha2Code === pais);
    const newName = umPais[0]
    this.setState({
      cards: [...cards, { name: newName.translations.pt, flag: newName.flag , local: local , meta: meta } ]
    })
  }

  handleEdit(e) {
    console.log('Edita');
  }

  handleDel(e) {
    let arrayCards = this.state.cards;
    arrayCards.splice(e.target.name,1);
    this.setState({ cards: arrayCards})
  }


  render() { 
    console.log('Render');
    const { paises, pais, local, meta, cards } = this.state
    return ( 
      <div className="App">
        <Header />
        {paises && <Search paises={ paises } pais={ pais } local={ local } meta={ meta } handleInput={ this.handleInput.bind(this) } handleAdd={ this.handleAdd.bind(this) } />}
        <CardCountry cards={ cards } handleEdit={ this.handleEdit.bind(this) } handleDel={ this.handleDel.bind(this)} />
      </div>

     );
  }
}

export default App;
