import React, { Component } from 'react';
import './App.css';
import Header from './components/Header';
import Search from './components/Search';
import Country from "./CountryDb";

class App extends Component {
  constructor() {
    super()
    this.state = {
      paises: undefined,
      pais: 'BR',
      local: 'Jampa',
      meta: '0000-00-00',
      cards: [ {nome: 'Brasil', flag: "https://restcountries.eu/data/bra.svg", local: 'Local: Jampa', meta: '05/12/2022'} ],
    }
  }

  async componentDidMount() {
    const paises = await Country();
    const { pais } = this.state
    this.setState({ paises: paises })
    const umPais = paises.filter(item => item.alpha2Code === pais);

    console.log(umPais);
  }

  handleInput(e) {
    const value = e.target.value;
    const key = e.target.name;
    this.setState({ [key]: value })
  }

  handleAdd() {
    const { pais, local, meta } = this.state
    console.log(pais, local, meta);
  }


  render() { 
    const { paises, pais, local, meta } = this.state
    return ( 
      <div className="App">
        <Header />
        {paises && <Search paises={ paises } pais={ pais } local={ local } meta={ meta } handleInput={ this.handleInput.bind(this) } handleAdd={ this.handleAdd.bind(this) } />}
      </div>
     );
  }
}

export default App;
