import React, { Component } from 'react';
import './App.css';
import Header from './components/Header';
import Search from './components/Search';
import Country from "./CountryDb";

class App extends Component {
  constructor() {
    super()
    this.state = {
      paises: undefined,
      pais: undefined,
      local: undefined,
      meta: undefined
    }
  }

  async componentDidMount() {
    const paises = await Country();
    this.setState({ paises: paises })
  }

  handleSelect(e) {
    e.target.value
  }

  render() { 
    const { paises } = this.state
    return ( 
      <div className="App">
        <Header />
        {paises && <Search paises={ paises } pais={ pais } handleSelect={ this.handleSelect.bind(this) } />}
      </div>
     );
  }
}

export default App;
